import React,{useState,useEffect} from "react";
import "./App.css"
import axios from "axios"
import Addtask from "../src/component/Addtask"
import Todolist from "./component/Todolist";
import Updatetask from '../src/components/Updatetask'
function App() {

  const [todoList, setTodoList] = useState([]);
  useEffect(() => {

    axios.get('http://localhost:8000/api/task').then(res => {
      setTodoList(res.data);
    }).catch(err => console.log(err));

  }, []);

  const addTask = newTask => {
    setTodoList([...todoList, newTask]);
  };

  const taskComplete = task => {
    const newList = [...todoList];
    newList.forEach(item => {
      if (item._id === task._id) {
        item.isComplete = task.isComplete;
      }
    });
    setTodoList(newList);
  }

  const removeTask = task => {
    const newList = todoList.filter(item => !(item._id === task._id))
    setTodoList(newList)
  }
    return (
      <div>
        <Addtask addTask={addTask} />
        <Todolist todoList={todoList}  removeTask={removeTask} />
      </div>
    );
  };

  export default App
